import React from 'react';

 class Region extends Component {
	render() {
		return(
		<form>
		选择地区  <div class="btn-group" role="group">
			<button type="button" class="btn btn-info"
			onClick={()=>this.props.setRegion('month-southern')}>南半球</button>
			<button type="button" class="btn btn-info"
			onClick={()=>this.props.setRegion('month-northern')}>北半球</button>
		</div>
		</form>
		);
	}
}
