// 			onClick={()=>setRegion('month-northern')}>北半球</button>
// 		</div>
// 		</form>
// 		);
// }

export const Month = ({setMonth}) => {
	const buttons = Array(12).fill().map((_,i)=> {
		const month = i + 1; 
		const element = <button key={i} type="button" class="btn btn-info"
			onClick={()=>setMonth(month)}>{month}</button>
		return element
	})
	return (
		<form>
		选择月份  <div class="btn-group" role="group">
			{buttons}
			</div>
		</form>

		);
}

export const Category = ({setCategory}) => {
	return(
		<form>
		查看类别  <div class="btn-group" role="group">
			<button type="button" class="btn btn-info"
			onClick={()=>setCategory('fish')}>鱼类</button>
			<button type="button" class="btn btn-info"
			onClick={()=>setCategory('bugs')}>昆虫类</button>
			<button type="button" class="btn btn-info"
			onClick={()=>setCategory('houseware')}>家具类</button>
			<button type="button" class="btn btn-info"
			onClick={()=>setCategory('villagers')}>居民类</button>
		</div>
		</form>
		);
}

export const FilterPrice = ({setRank}) => {
	return (
		<div class="btn-group" role="group">
			<button type="button" class="btn btn-sm btn-secondary" onClick={()=>setRank(0)}>图鉴顺序</button>
			<button type="button" class="btn btn-sm btn-secondary" onClick={()=>setRank(1)}>价格从高到低</button>
			<button type="button" class="btn btn-sm btn-secondary" onClick={()=>setRank(-1)}>价格从低到高</button>
		</div>	
		)
}

export default Region;
