import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import {Region, Month, Category} from '../components/Filter';
import Scroll from '../components/Scroll';
import './App.css';

class App extends Component {
  constructor() {
    super()
    this.state = {
      fish: [],
      searchfield: '',
      region: '',
      month: '',
      category: ''
    }
  }

  componentDidMount() {
    fetch('https://acnhapi.com/v1/fish/')
      .then(response=> response.json())
      .then(items => {this.setState({ fish: items})});
  }

  onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value })
  }

  setFilter = (region,month,category) => {
    this.setState({region: region})
  }

  render() {
    const { fish, searchfield } = this.state;
    const fish_array = Object.entries(fish)
    let filteredfish = fish_array.filter(fish =>{
      return fish[1].name['name-CNzh'].includes(searchfield);
    })
    return !fish_array.length ?
      <h1>Loading</h1> :
      (
        <div className='tc'>
          <h1 className='f1'>Animal Crossing</h1>
          <Region setRegion={this.setFilter}/>
          <SearchBox searchChange={this.onSearchChange}/>
          <Scroll>
            <CardList fish={filteredfish} region={this.state.region}/>
          </Scroll>
        </div>
      );
  }
}

export default App;
