import React from 'react';

const Card = ({ name, price, id, time, month, region }) => {
  const fish_id = id+1
  const time_display = time?time:'常见'
  let month_display = '请选择南北半球'
  if (region !== '') 
  	{month_display = month?month:'常见'} 

  return (
    <div className='tc grow bg-light-green fl w-10 pa2 '>
      <img alt='fish' src={`https://acnhapi.com/v1/images/fish/${fish_id}?size=200x200`} />
      <div>
        <h4>{name}</h4>
        <p>Price: {price}</p>
        <p>Time: {time_display}</p>
        <p>Month: {month_display}</p>
      </div>
    </div>
  );
}

export default Card;
