import React from 'react';

 class Region extends React.Component {
 	constructor(props){
 		super(props)
 		this.state = {
 			clicked: false
 		}
 		this.handleClick = this.handleClick.bind(this)
 	}
 	handleClick(){
 		this.setState({
 			button:!this.state.button
 		})
 	}

	render() {
		return(
		<form>
		选择地区  <div className="btn-group" role="group">
			<button type="button" className={this.state.button?"buttonFalse":''}
			onClick={()=>{this.handleClick()}}>南半球</button>
			<button type="button" className="btn btn-info"
			onClick={()=>this.props.setRegion('month-northern')}>北半球</button>
		</div>
		</form>
		);
	}
}
