import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import {Region, Month, Category, FilterPrice} from '../components/Filter';
// import Scroll from '../components/Scroll';
import './App.css';


class App extends Component {
  constructor() {
    super()
    this.state = {
      fish: [],
      searchfield: '',
      region: '',
      month: '',
      category: '',
      highToLow: ''
    }
  }



  componentDidMount() {
    fetch('https://acnhapi.com/v1/fish/')
      .then(response=> response.json())
      .then(items => {this.setState({ fish: items})});
  }

  onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value })
  }

  setRegion = (region) => {
    this.setState({region: region});
  }

  setMonth = (month) => {
    this.setState({month:month})
  }

  setCategory = (category) => {
    this.setState({category:category})
  }

  setRank = () => {
    this.setState({highToLow: this.state.highToLow?false:true})
  }

  filterItem = (array) => {
     let filteredItem = array.filter(a =>{
      return a[1].name['name-CNzh'].includes(searchfield);
    })
    if (this.state.highToLow !== ''){
      if (this.state.highToLow) {
        filteredItem = filteredItem.sort(function(a,b){return b[1].price-a[1].price}) 
      }
      else {
        filteredItem = filteredItem.sort(function(a,b){return a[1].price-b[1].price}) 
      }
    }
    return filteredItem
  } 

  render() {
    const { fish, searchfield } = this.state;
    let fish_array = Object.entries(fish);
    fish_array = filterItem(fish_array);

    return !fish_array.length ?
      <h1 className='tc'>Loading</h1> :
      (
        <div className='tc'>
          <h1 className='f1'>Animal Crossing</h1>
            <div className="row">
              <Region setRegion={this.setRegion}/>
              <Month setMonth={this.setMonth}/>
              <Category setCategory={this.setCategory}/>  
              <SearchBox searchChange={this.onSearchChange}/>
          </div> 
          <div className="filter">
          <FilterPrice setRank={this.setRank}/>
          </div>
            <div className="container">
            <CardList fish={filteredfish} region={this.state.region}/>
            </div>
        </div>
      );
  }
}

export default App;
